SELECT title FROM (SELECT movies.title, movies.id, people.id, people.name FROM movies INNER JOIN stars ON movies.id = stars.movie_id INNER JOIN people ON stars.person_id = people.id) WHERE name ="Helena Bonham Carter" AND title IN(SELECT title FROM (SELECT movies.title, movies.id, people.id, people.name FROM movies INNER JOIN stars ON movies.id = stars.movie_id INNER JOIN people ON stars.person_id = people.id) WHERE name ="Johnny Depp")